    private void btncalculateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btncalculateActionPerformed
    Scanner keyedInput = new Scanner (System.in);
    double [ ] newtotal = new double [5];
    double vrobits;
    double [ ] drobzit = new double [2];
    double [ ] clickwick = new double [2];
    double [ ] gazoontight = new double [2];
    double [ ] frazoint = new double [2];
    double [ ] blointoint = new double [2];
    vrobits = Integer.parseInt(txtfvrobits.getText());
    drobzit[0] = vrobits/100000;
    drobzit[1] = Math.floor(drobzit[0]);
    txtfdrobzits.setText(""+drobzit[1]+"");
    newtotal[0] = (Math.abs(drobzit[0]-drobzit[1])) *vrobits;
    
    clickwick[0] = newtotal[0]/50000;
    clickwick[1] = Math.floor(clickwick[0]);
    txtfclickwick.setText(""+clickwick[1]+"");
    newtotal[1] = (Math.abs(clickwick[0]-clickwick[1]))*newtotal[0];
    
    gazoontight[0] = newtotal[1]/10000;
    gazoontight[1] = Math.floor(gazoontight[0]);
    txtfgazoontight.setText(""+gazoontight[1]+"");
    newtotal[2] = (Math.abs(gazoontight[0]-gazoontight[1]))*newtotal[1];
    
    frazoint[0] = newtotal[2]/1000;
    frazoint[1] = Math.floor(frazoint[0]);
    txtffrazoint.setText(""+frazoint[1]+"");
    newtotal[3] = (Math.abs(frazoint[0]-frazoint[1]))*newtotal[2];
    
    blointoint[0] = newtotal[3]/500;
    blointoint[1] = Math.floor(blointoint[0]);
    txtfblointoint.setText(""+blointoint[1]+"");
    newtotal[4] = (Math.abs(blointoint[0]-blointoint[1]))*newtotal[3];
    
    txtfleftover.setText(""+newtotal[4]+"");
    txtfcomment.setText("you should of got "+drobzit[1]+" Drobzits, "+clickwick[1]+"Clickwicks, "+gazoontight[1]+"Gazoontights, "+frazoint[1]+"Frazoints, "+blointoint[1]+"Blointoints and "+newtotal[4]+"as a leftover");
    }//GEN-LAST:event_btncalculateActionPerformed

